                    this.map.openInfoWindow(
                      infoWindow,
                      new BMap.Point(this.whichProductData.lng, this.whichProductData.lat)
                    ); // 开启信息窗口
                  });
      this.drawOil();
    },
    closeSelect() {
      this.selectShow = false;
    },
    accOpen() {
      //   console.log(this.whichProductData);
      console.log(this.productDetailList);
      this.productDetailList.forEach(item => {
        if (this.whichProductData.productName == item.productName) {
          var accDeviceKey = "";
          item.deviceData.forEach(item1 => {
            if (item1.deviceName == "ACC") {
              accDeviceKey = item1.deviceKey;
              var endTime = Math.round(new Date() / 1000);
              var startTime = endTime - 7200 * 12;
              var data = {
                deviceKey: accDeviceKey,
                startTime: startTime,
                endTime: endTime,
                asc: 1
              };
              getDeviceHisData(data).then(res => {
                console.log(res);
                if (res.msg == "未找到记录") {
                  this.$message.info("一天内没有该车辆震动的记录数据");
                }
                if (res.msg == "ok") {
                  this.accHistory = true;

                  var accList = [];
                  var accDate = [];
                  var accData = [];
                  if (res.data.deviceData.length > 100) {
                    accList = res.data.deviceData.slice(
                      res.data.deviceData.length - 100,
                      res.data.deviceData.length
                    );
                  } else {
                    accList = res.data.deviceData;
                  }
                  // console.log(accList);
                  if (res.data.deviceData == 0) {
                    this.accData = false;
                  } else {
                    this.accData = true;
                    accList.forEach(item => {
                      accDate.push(item.date);
                      accData.push(item.acc.X + item.acc.Y + item.acc.Z);
                    });
                    console.log(accData);
                    console.log(accDate);
                    var chartDom = document.getElementById("acc");
                    var myChart = echarts.init(chartDom);
                    var option;

                    option = {
                      legend: {
                        data: accDate
                      },
                      tooltip: {
                        trigger: "axis"
                      },
                      xAxis: {
                        type: "category",
                        axisTick: {
                          alignWithLabel: true
                        },
                        data: accDate,
                        axisLabel: {
                          formatter: function(value) {
                            return (
                              value.slice(5, 10) + "\n" + value.slice(11, 16)
                            );
                          },
                          textStyle: {
                            color: "#fff" //坐标值得具体的颜色
                          }
                        }
                      },
                      yAxis: {
                        type: "value",
                        axisLabel: {
                          formatter: "{value}",
                          textStyle: {
                            color: "#fff" //坐标值得具体的颜色
                          }
                        },
                        axisLine: {
                          show: true, // Y轴线
                          lineStyle: {
                            type: "dashed",
                            color: "#fff"
                          }
                        }
                      },
                      series: [
                        {
                          data: accData,
                          type: "line",
                          smooth: true
                        }
                      ]
                    };

                    option && myChart.setOption(option);
                    window.addEventListener("resize", function() {
                      myChart.resize();
                    });
                  }
                }
              });
            }
          });
        }
      });
    },
